package net.onlineshop.services.front.emailNotifyProduct.impl;import net.onlineshop.core.ServersManager;import net.onlineshop.services.front.emailNotifyProduct.EmailNotifyProductService;import net.onlineshop.services.front.emailNotifyProduct.bean.EmailNotifyProduct;import net.onlineshop.services.front.emailNotifyProduct.dao.EmailNotifyProductDao;public class EmailNotifyProductServiceImpl extends		ServersManager<EmailNotifyProduct> implements EmailNotifyProductService {	private EmailNotifyProductDao emailNotifyProductDao;	public void setEmailNotifyProductDao(			EmailNotifyProductDao emailNotifyProductDao) {		this.emailNotifyProductDao = emailNotifyProductDao;	}	@Override	public int selectCount(EmailNotifyProduct ep) {		return emailNotifyProductDao.selectCount(ep);	}}