package net.onlineshop.web.action.front.questionnaireResult;import net.onlineshop.core.BaseAction;import net.onlineshop.services.front.questionnaireResult.QuestionnaireResultService;import net.onlineshop.services.front.questionnaireResult.bean.QuestionnaireResult;/** * 问卷结果 *  * */public class QuestionnaireResultAction extends BaseAction<QuestionnaireResult> {	private static final long serialVersionUID = 1L;	private QuestionnaireResultService questionnaireResultService;	public QuestionnaireResultService getQuestionnaireResultService() {		return questionnaireResultService;	}	protected void selectListAfter() {		pager.setPagerUrl("questionnaireResult!selectList.action");	}	public void setQuestionnaireResultService(			QuestionnaireResultService questionnaireResultService) {		this.questionnaireResultService = questionnaireResultService;	}	public QuestionnaireResult getE() {		return this.e;	}	public void prepare() throws Exception {		if (this.e == null) {			this.e = new QuestionnaireResult();		}	}	public void insertAfter(QuestionnaireResult e) {		e.clear();	}}