package net.onlineshop.web.action.manage.hotquery;import net.onlineshop.core.BaseAction;import net.onlineshop.services.manage.hotquery.HotqueryService;import net.onlineshop.services.manage.hotquery.bean.Hotquery;public class HotqueryAction extends BaseAction<Hotquery> {	private static final long serialVersionUID = 1L;	private HotqueryService hotqueryService;	public HotqueryService getHotqueryService() {		return hotqueryService;	}	protected void selectListAfter() {		pager.setPagerUrl("hotquery!selectList.action");	}	public void setHotqueryService(HotqueryService hotqueryService) {		this.hotqueryService = hotqueryService;	}	public Hotquery getE() {		return this.e;	}	public void prepare() throws Exception {		if (this.e == null) {			this.e = new Hotquery();		}	}	public void insertAfter(Hotquery e) {		e.clear();	}}